/** @format */

export default handleGroupMsg = async (msg) => {
	await axios.post(sendMessageGroup, {
		name: currentChat.name,
		from: currentUser._id,
		messages: msg,
	});
	//

	socket.current.emit("message", {
		to: currentChat._id,
		from: currentUser._id,
		message: msg,
	});

	//
	const msgs = [...groupMessages];

	console.log(`messages :${JSON.stringify(msgs)}`);
	msgs.push({
		fromSelf: true,
		message: msg,
	});
	console.log(`before msgs :${msgs}`);
	setGroupMessages(msgs);
};
