import React, { useState, useEffect, useRef } from "react";
import styled from "styled-components";
import ChatInput from "./ChatInput";
import Logout from "./Logout";
import axios from "axios";
import {
	getAllMessagesRoute,
	sendMessageRoute,
	allChats,
	sendMessageGroup,
	groupchatmessage,
} from "../utils/APIRoutes";
import { v4 as uuidv4 } from "uuid";

export default function ChatContainer({
	currentChat,
	currentUser,
	socket,
	groupChatName,
	activeComponent,
}) {
	console.log(`current chat :${JSON.stringify(currentChat)}`);
	// console.log(`current user : ${JSON.stringify(currentUser)}`);
	const [messages, setMessages] = useState([]);
	const [arrivalMessage, setArrivalMessage] = useState(null);
	const [arrivalGroupMessage, setArrivalGroupMessage] = useState(null);
	const [groupmessages, setGroupmessages] = useState([]);

	const scrollRef = useRef();

	useEffect(() => {
		const fetchData = async () => {
			if (currentChat) {
				const response = await axios.post(getAllMessagesRoute, {
					from: currentUser._id,
					to: currentChat._id,
				});
				setMessages(response.data);
				console.log(response);
				//fetching single person chat Messages from db
				console.log("chat message :" + JSON.stringify(response.data));
			}
		};
		fetchData();
	}, [currentChat]);

	useEffect(() => {
		const fetchGroup = async () => {
			if (currentChat) {
				const response = await axios.post(groupchatmessage, {
					from: currentUser._id,
					to: currentChat.name,
				});
				setGroupmessages(response.data);
			}
		};
		fetchGroup();
	}, [currentChat]);
	console.log(`group chat data :${JSON.stringify(groupmessages.messages)}`);
	for (let key in groupmessages) {
		console.log(`message :${JSON.stringify(groupmessages.key)}`);
	}

	const handleSendMsg = async (msg) => {
		await axios.post(sendMessageRoute, {
			from: currentUser._id,
			to: currentChat._id,
			message: msg,
		});
		socket.current.emit("send-msg", {
			to: currentChat._id,
			from: currentUser._id,
			message: msg,
		});

		const msgs = [...messages];
		console.log(`messages :${JSON.stringify(msgs)}`);
		msgs.push({
			fromSelf: true,
			message: msg,
		});
		setMessages(msgs);
		// post message to db and emitting server
		//console.log("sent Message :" + JSON.stringify(msgs));
	};

	const handleGroupMsg = async (msg) => {
		await axios.post(sendMessageGroup, {
			from: currentUser._id,
			to: currentChat.name,
			messages: msg,
		});
		const msgs = [...messages];
		console.log(`messages :${JSON.stringify(msgs)}`);
		msgs.push({
			fromSelf: true,
			message: msg,
		});
		setGroupmessages(msgs);
	};

	console.log(`group messages : ${groupmessages}`);

	useEffect(() => {
		if (socket.current) {
			socket.current.on("msg-recieved", (msg) => {
				setArrivalMessage({
					fromSelf: false,
					message: msg,
				});
			});
		}
	}, []);

	useEffect(() => {
		arrivalMessage && setMessages((prev) => [...prev, arrivalMessage]);
	}, [arrivalMessage]);

	useEffect(() => {
		arrivalGroupMessage &&
			setGroupmessages((prev) => [...prev, arrivalGroupMessage]);
	}, [arrivalGroupMessage]);

	useEffect(() => {
		scrollRef.current?.scrollIntoView({ behavior: "smooth" });
	}, [messages]);

	console.log(`arrivalgroup message :${arrivalGroupMessage}`);

	let chats = false;
