/** @format */

import React, { useState, useEffect, useRef } from "react";
import styled from "styled-components";
import ChatInput from "./ChatInput";
import Logout from "./Logout";
import axios from "axios";
import {
	getAllMessagesRoute,
	sendMessageRoute,
	allChats,
	sendMessageGroup,
	groupchatmessage,
} from "../utils/APIRoutes";
import { v4 as uuidv4 } from "uuid";

export default function ChatContainer({
	currentChat,
	currentUser,
	socket,
	groupChatName,
	activeComponent,
}) {
	// console.log(`current chat :${JSON.stringify(messages)}`);
	// console.log(`current user : ${JSON.stringify(currentUser)}`);
	const [messages, setMessages] = useState([]);
	const [arrivalMessage, setArrivalMessage] = useState(null);
	const [arrivalGroupMessage, setArrivalGroupMessage] = useState(null);
	const [groupmessages, setGroupmessages] = useState([]);

	messages.map((user) => {
		console.log("check messages : " + JSON.stringify(user));
	});
	console.log(`groupmessages : ${typeof groupmessages}`);

	const scrollRef = useRef();

	useEffect(() => {
		const fetchData = async () => {
			if (currentChat) {
				const response = await axios.post(getAllMessagesRoute, {
					from: currentUser._id,
					to: currentChat._id,
				});
				setMessages(response.data);
				console.log(response);
				//fetching single person chat Messages from db
				console.log("chat message :" + JSON.stringify(response.data));
			}
		};
		fetchData();
	}, [currentChat]);

	useEffect(() => {
		const fetchGroup = async () => {
			if (currentChat) {
				const response = await axios.post(groupchatmessage, {
					from: currentUser._id,
					to: currentChat.name,
				});
				setGroupmessages(response.data);
			}
		};
		fetchGroup();
	}, [currentChat]);
	// console.log(`group chat data :${JSON.stringify(groupmessages.messages)}`);
	// for (let key in groupmessages) {
	// 	console.log(`message :${JSON.stringify(groupmessages.key)}`);
	// }

	const handleSendMsg = async (msg) => {
		await axios.post(sendMessageRoute, {
			from: currentUser._id,
			to: currentChat._id,
			message: msg,
		});
		socket.current.emit("send-msg", {
			to: currentChat._id,
			from: currentUser._id,
			message: msg,
		});

		const msgs = [...messages];
		console.log(`messages :${JSON.stringify(msgs)}`);
		msgs.push({
			fromSelf: true,
			message: msg,
		});
		setMessages(msgs);
		// post message to db and emitting server
		//console.log("sent Message :" + JSON.stringify(msgs));
	};

	const handleGroupMsg = async (msg) => {
		await axios.post(sendMessageGroup, {
			from: currentUser._id,
			to: currentChat.name,
			messages: msg,
		});
		const msgs = [...messages];
		console.log(`messages :${JSON.stringify(msgs)}`);
		msgs.push({
			fromSelf: true,
			message: msg,
		});
		setGroupmessages(msgs);
	};

	useEffect(() => {
		if (socket.current) {
			socket.current.on("msg-recieved", (msg) => {
				setArrivalMessage({
					fromSelf: false,
					message: msg,
				});
			});
		}
	}, []);

	useEffect(() => {
		arrivalMessage && setMessages((prev) => [...prev, arrivalMessage]);
	}, [arrivalMessage]);

	useEffect(() => {
		arrivalGroupMessage &&
			setGroupmessages((prev) => [...prev, arrivalGroupMessage]);
	}, [arrivalGroupMessage]);

	useEffect(() => {
		scrollRef.current?.scrollIntoView({ behavior: "smooth" });
	}, [messages]);

	let chats = false;
	// console.log(`arrivalgroup message :${arrivalGroupMessage}`);

	// console.log(`check chats : ${chats}`);
	//console.log("Group chat name : " + JSON.stringify(groupChatName));
	// for (let key in groupChatName) {
	// 	console.log(`groupchat Name : ${groupChatName[key].name}`);
	// }
	//console.log(JSON.stringify(messages));
	//console.log(`current chat data :  ${JSON.stringify(currentChat)}`);

	return (
		<>
			{chats
				? currentChat && (
						<Container>
							<div className="chat-header">
								<div className="user-details">
									<div className="avatar">
										<img
											src={`data:image/svg+xml;base64,${currentChat.avatarImage}`}
											alt="avatar"
										/>
									</div>
									<div className="username">
										<h3>{currentChat.username}</h3>
									</div>
								</div>
								<Logout />
							</div>
							<div className="chat-messages">
								{messages.map((message) => {
									return (
										<div ref={scrollRef} key={uuidv4()}>
											<div
												className={`message ${
													message.fromSelf ? "sended" : "recieved"
												}`}>
												<div className="content ">
													<p>{message.message}</p>
												</div>
											</div>
										</div>
									);
								})}
							</div>
							<ChatInput handleSendMsg={handleSendMsg} />
						</Container>
				  )
				: groupChatName && (
						<Container>
							<div className="chat-header">
								<div className="user-details">
									<div className="avatar">
										<img
											src={`data:image/svg+xml;base64,${currentChat.avatarImage}`}
											alt="avatar"
										/>
									</div>
									<div className="username">
										<h3>{currentChat.name}</h3>
									</div>
								</div>
								<Logout />
							</div>
							<div className="chat-messages">
								{Object.keys(groupmessages).map((message) => {
									return (
										<div ref={scrollRef} key={uuidv4()}>
											<div
												className={`message ${
													message.fromSelf ? "sended" : "recieved"
												}`}>
												<div className="content ">
													<p>{messages.message}</p>
												</div>
											</div>
										</div>
									);
								})}
							</div>
							<ChatInput handleGroupMsg={handleGroupMsg} />
						</Container>
				  )}
		</>
	);
}

const Container = styled.div`
	display: grid;
	grid-template-rows: 10% 80% 10%;
	gap: 0.1rem;
	overflow: hidden;
	@media screen and (min-width: 720px) and (max-width: 1080px) {
		grid-template-rows: 15% 70% 15%;
	}
	.chat-header {
		display: flex;
		justify-content: space-between;
		align-items: center;
		padding: 0 2rem;
		.user-details {
			display: flex;
			align-items: center;
			gap: 1rem;
			.avatar {
				img {
					height: 3rem;
				}
			}
			.username {
				h3 {
					color: white;
					text-transform: capitalize;
				}
			}
		}
	}
	.chat-messages {
		padding: 1rem 2rem;
		display: flex;
		flex-direction: column;
		gap: 1rem;
		overflow: auto;
		&::-webkit-scrollbar {
			width: 0.2rem;
			&-thumb {
				background-color: #ffffff39;
				width: 0.1rem;
				border-radius: 1rem;
			}
		}
		.message {
			display: flex;
			align-items: center;
			.content {
				max-width: 40%;
				overflow-wrap: break-word;
				padding: 1rem;
				font-size: 1.1rem;
				border-radius: 1rem;
				color: #d1d1d1;
				@media screen and (min-width: 720px) and (max-width: 1080px) {
					max-width: 70%;
				}
			}
		}
		.sended {
			justify-content: flex-end;
			.content {
				background-color: #4f04ff21;
			}
		}
		.recieved {
			justify-content: flex-start;
			.content {
				background-color: #9900ff20;
			}
		}
	}
`;
